const navigation=document.querySelector(".navigation"),openButton=document.querySelector(".header__toggle"),closeButton=navigation.querySelector(".navigation__close-button"),hideNavigation=()=>{closeButton.classList.remove("navigation__close-button--show"),navigation.classList.remove("navigation--active")},showNavigation=()=>{closeButton.classList.add("navigation__close-button--show"),navigation.classList.add("navigation--active")},hideNoJsNavigation=()=>{navigation.classList.remove("navigation--no-js")};document.addEventListener("DOMContentLoaded",function(){openButton.classList.add("header__toggle--show"),navigation.classList.remove("navigation--no-js"),hideNavigation()}),openButton.addEventListener("click",showNavigation),closeButton.addEventListener("click",hideNavigation);